mapper.insert:
/* id="inertMoneykeeping" [가계부 insert문 ] [박다빈 / 2020-10-20] */
INSERT INTO MONEYSAVE_CONTENT(
	  years
	 ,months
	 ,days
	 ,P_txt
	 ,P_price
	 ,M_txt
	 ,M_price
	 ,regdate
	 ,delYn
	 ,flag
	 ,fixe
) VALUES 
	
		(
	 	 #{item.years}
	 	,#{item.months}
	 	,#{item.days}
		,#{item.P_txt}
 		,#{item.P_price}
		,#{item.M_txt}
 		,#{item.M_price}
	 	,now()
	 	,'N'
	 	,#{item.flag}
	 	,#{item.fixe}
	 	)

mapper.select:
/* id="getList" [년,월별 가계부 data 갖고오기] [박다빈 / 2020-10-20] */ SELECT x.* FROM (SELECT years ,months ,days ,IFNULL(P_txt, 0) as P_txt ,IFNULL(P_price, 0) as P_price ,IFNULL(M_txt, 0) as M_txt ,IFNULL(M_price, 0) as M_price ,flag ,fixe ,fseq FROM MONEYSAVE_CONTENT WHERE 1=1 AND years = #{years} AND months = #{months} AND delYn ='N' AND fixe ='N'
UNION ALL
SELECT years ,months ,days ,IFNULL(P_txt, 0) as P_txt ,IFNULL(P_price, 0) as P_price ,IFNULL(M_txt, 0) as M_txt ,IFNULL(M_price, 0) as M_price ,flag ,fixe ,fseq FROM MONEYSAVE_CONTENT WHERE 1=1 AND delYn ='N' AND fixe ='Y') X ORDER BY X.days ASC

mapper.update:
/* id="delte" [삭제] [박다빈 / 2020-10-20] */
UPDATE moneysave_content 
SET 
	delYn ='Y'
WHERE	
	fseq = #{fseq}

/* id="update" [업데이트] [박다빈 / 2020-10-20] */
UPDATE moneysave_content 
SET 
	 P_txt 		= #{p_txt}
	,P_price 	= #{P_price}
	,M_txt 		= #{M_txt}
	,M_price 	= #{M_price}
WHERE
	fseq = #{fseq}
